import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import styled from "styled-components";
import bg from '../../img/bg.png';
import { MainLayout } from '../../styles/Layouts';
import Navigation from '../Navigation/Navigation';
import Dashboard from '../Dashboard/Dashboard';
import Login from '../Pages/Login';
import Income from '../Income/Income';

function MainApp() {
  const [active, setActive] = useState(1);

  const displayData = () => {
    if (!user) { // Verifica si el usuario está autenticado
      return <Navigate to="/login" />; // Redirige a la página de inicio de sesión si el usuario no está autenticado
    }

      switch(active) {
          case 1:
              return <Dashboard />;
          case 2:
              return <Income />;
          default: 
              return <Dashboard />;
      }
  }

  return (
    <AppStyled bg={bg} className="App">
      <MainLayout>
        <Navigation active={active} setActive={setActive} />
        <main>
          {displayData()} {/* Muestra el componente según el valor de active */}
        </main>
      </MainLayout>
    </AppStyled>
  );
}

const AppStyled = styled.div`
  height: 100vh;
  background-image: url(${props => props.bg});
  position: relative;
  main {
    flex: 1;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    overflow-x: hidden;
    &::-webkit-scrollbar {
      width: 0;
    }
  }
`;

export default MainApp;
