import React from 'react';
import styled from "styled-components";
import bg from './img/bg.png';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'; // Importa BrowserRouter y Route
import { MainLayout } from './styles/Layouts';
import Navigation from './Components/Navigation/Navigation';
import Dashboard from './Components/Dashboard/Dashboard';
import Income from './Components/Income/Income';
import Login from './Components/Pages/Login';

function App() {
  return (
    <Router> {/* Envuelve tu aplicaci√≥n con BrowserRouter */}
      <AppStyled bg={bg} className="App">
        <MainLayout>
          <Navigation />
          <main>
            <Switch> {/* Switch asegura que solo se renderice una ruta a la vez */}
              <Route path="/" exact component={Dashboard} /> {/* Ruta para el Dashboard */}
              <Route path="/income" component={Income} /> {/* Ruta para el Income */}
              <Route path="/login" component={Login} /> {/* Ruta para el Login */}
            </Switch>
          </main>
        </MainLayout>
      </AppStyled>
    </Router>
  );
}

const AppStyled = styled.div`
  height: 100vh;
  background-image: url(${props => props.bg});
  position: relative;
  main{
    flex: 1;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    overflow-x: hidden;
    &::-webkit-scrollbar{
      width: 0;
    }
  }
`;

export default App;
