import React, { useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import styled from "styled-components";
import bg from '../../img/bg.png';
import { MainLayout } from '../../styles/Layouts';
import Navigation from '../Navigation/Navigation';
import Dashboard from '../Dashboard/Dashboard';
import Income from '../Income/Income';
import Login from './Login';

function MainApp() {
  const [active, setActive] = useState(""); // Cambiar a una cadena vacía inicialmente
  const navigate = useNavigate(); // Obtener la función de navegación

  // Función para manejar el clic en las opciones de la barra de navegación
  const handleNavigation = (route) => {
    setActive(route); // Actualizar el estado activo al hacer clic en una opción
    navigate(route); // Navegar a la ruta correspondiente
  }

  // Función para mostrar el componente según la opción seleccionada
  const displayData = () => {
    switch(active) {
      case "dashboard":
        return <Dashboard />;
      case "income":
        return <Income />;
      default: 
        return <Dashboard />; // Por defecto, mostrar el Dashboard
    }
  }

  return (
    <AppStyled bg={bg} className="App">
      <MainLayout>
        <Navigation active={active} handleNavigation={handleNavigation} />
        <main>
          {displayData()} {/* Mostrar el componente según la opción seleccionada */}
        </main>
      </MainLayout>
    </AppStyled>
  );
}

const AppStyled = styled.div`
  height: 100vh;
  background-image: url(${props => props.bg});
  position: relative;
  main {
    flex: 1;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    overflow-x: hidden;
    &::-webkit-scrollbar {
      width: 0;
    }
  }
`;

export default MainApp;
