import React, {useState, useMemo} from 'react'
import styled from "styled-components";
import bg from './img/bg.png'
import {MainLayout} from './styles/Layouts'
import Navigation from './Components/Navigation/Navigation'
import Dashboard from './Components/Dashboard/Dashboard';
import Income from './Components/Income/Income'
import Login from './Components/Pages/Login';


function App() {
  const [active, setActive] = useState(1)


  const displayData = () => {
    switch(active){
      case 1:
        return <Dashboard />
      case 2:
        return <Income />
      default: 
      return <Dashboard />
    }
  }

  return (
    <Router> {/* Envuelve tu componente con BrowserRouter */}
      <AppStyled bg={bg} className="App">
        <MainLayout>
          <Navigation isLoggedIn={isLoggedIn} /> {/* Pasa el estado de autenticaci칩n a Navigation */}
          <main>
            {/* Mostrar el componente de inicio de sesi칩n si el usuario no est치 autenticado */}
            {!isLoggedIn && <Login setIsLoggedIn={setIsLoggedIn} />}
            {/* Mostrar el componente deseado si el usuario est치 autenticado */}
            {isLoggedIn && <Route path="/dashboard" component={Dashboard} />}
            {isLoggedIn && <Route path="/income" component={Income} />}
          </main>
        </MainLayout>
      </AppStyled>
    </Router>
  );
}

const AppStyled = styled.div`
  height: 100vh;
  background-image: url(${props => props.bg});
  position: relative;
  main{
    flex: 1;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    overflow-x: hidden;
    &::-webkit-scrollbar{
      width: 0;
    }
  }
`;

export default App;
